INSERT INTO `activities` (`id`, `subject`, `sub_title`, `detail`, `image`, `status`, `datetime`) VALUES
(1, 'กิจกรรมที่ 1', 'วันที่ 16 มิถุนายน 2562 โรงเรียนชาวนาบ้านคลองรี่ ร่วมงานการพัฒนางานวิจัย', '<p><span style=\"font-size:small\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">วันที่ 16 มิถุนายน 2562 โรงเรียนชาวนาบ้านคลองรี่ ร่วมงานการพัฒนางานวิจัย</span></span></span></p>\r\n\r\n<div style=\"-webkit-text-stroke-width:0px; text-align:start; text-indent:0px\"><span style=\"font-size:small\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">กับทีมนักวิจัยของมหาวิทยาลัยราชภัฏจันทรเกษม&nbsp;โดยมี ศ.ดร. พรรณ๊ บัวเล็ก เป็นหัวหน้าโครงการ ร่วมด้วย ดร.สุพรรณี ปลัดศรีช่วย และ ดร.วิจิตร จารุโณประถัมภ์</span></span></span></div>\r\n\r\n<div style=\"-webkit-text-stroke-width:0px; text-align:start; text-indent:0px\">&nbsp;</div>\r\n\r\n<div style=\"-webkit-text-stroke-width:0px; text-align:center; text-indent:0px\"><img alt=\"\" class=\"img-fluid\" src=\"https://klongri-ffs.com/klongri/admin/assets/images/source/%E0%B8%81%E0%B8%B4%E0%B8%88%E0%B8%81%E0%B8%A3%E0%B8%A3%E0%B8%A1%201/C%2001.jpg\" style=\"height:100%; width:100%\" /></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"img-fluid\" src=\"https://klongri-ffs.com/klongri/admin/assets/images/source/%E0%B8%81%E0%B8%B4%E0%B8%88%E0%B8%81%E0%B8%A3%E0%B8%A3%E0%B8%A1%201/C%2002.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"img-fluid\" src=\"https://klongri-ffs.com/klongri/admin/assets/images/source/%E0%B8%81%E0%B8%B4%E0%B8%88%E0%B8%81%E0%B8%A3%E0%B8%A3%E0%B8%A1%201/C%2003.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"img-fluid\" src=\"https://klongri-ffs.com/klongri/admin/assets/images/source/%E0%B8%81%E0%B8%B4%E0%B8%88%E0%B8%81%E0%B8%A3%E0%B8%A3%E0%B8%A1%201/C%2008.jpg\" style=\"height:100%; width:100%\" /></p>\r\n', 'klongri_102961931920191117_002328.jpg', 'true', '2019-11-16 17:23:28'),
(2, 'กิจกรรมที่ 2', 'ระหว่างวันที่ 26-27 สิงหาคม 2562 ไปศึกษาดูงาน ณ นาบุญข้าวหอม', '<p>ระหว่างวันที่&nbsp;26-27&nbsp;สิงหาคม&nbsp;2562&nbsp;ไปศึกษาดูงาน ณ นาบุญข้าวหอม และโครงการศึกษาวิจัยและพัฒนาสิ่งแวดล้อมอันเนื่องมาจากพระราชดำริ ตำบลแหลมผักเบี้ย อำเภอบ้านแหลม จังหวัดเพชรบุรี&nbsp;ร่วมกับเกษตรอำเภอสรรคบุรี และทีมนักวิจัยมหาวิทยาลัยราชภัฏจันทรเกษม&nbsp; &nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"img-fluid\" src=\"https://www.klongri-ffs.com/klongri/admin/assets/images/source/%E0%B8%81%E0%B8%B4%E0%B8%88%E0%B8%81%E0%B8%A3%E0%B8%A3%E0%B8%A1%202/Pet%2011.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"img-fluid\" src=\"https://www.klongri-ffs.com/klongri/admin/assets/images/source/%E0%B8%81%E0%B8%B4%E0%B8%88%E0%B8%81%E0%B8%A3%E0%B8%A3%E0%B8%A1%202/Pet%2014.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"img-fluid\" src=\"https://www.klongri-ffs.com/klongri/admin/assets/images/source/%E0%B8%81%E0%B8%B4%E0%B8%88%E0%B8%81%E0%B8%A3%E0%B8%A3%E0%B8%A1%202/Pet%2021.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"img-fluid\" src=\"https://www.klongri-ffs.com/klongri/admin/assets/images/source/%E0%B8%81%E0%B8%B4%E0%B8%88%E0%B8%81%E0%B8%A3%E0%B8%A3%E0%B8%A1%202/Pet%2023.jpg\" style=\"height:100%; width:100%\" /></p>\r\n', 'klongri_130538075420191117_004056.jpg', 'true', '2019-11-16 17:40:56'),
(3, 'กิจกรรมที่ 3', 'วันที่ 19 กันยายน 2562 ร่วมกิจกรรมสนทนากลุ่ม', '<p>วันที่ 19 กันยายน 2562 ร่วมกิจกรรมสนทนากลุ่มเรื่อง แนวทางการพัฒนาหลักสูตร และการบริหารจัดการโรงเรียนชาวนา ณ ศูนย์การศึกษามหาวิทยาลัยราชภัฏจันทรเกษม-ชัยนาท มี ผู้ช่วยศาสตราจารย์ ดร. สถาพร ถาวรอธิวาสน์ นางละมูล จันทร์วงค์ และนายสุนทร มณฑา เป็นวิทยากรเสวนา โดยมี ดร.วิจิตร จารุโณประถัมภ์ ดำเนินรายการ</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"img-fluid\" src=\"https://www.klongri-ffs.com/klongri/admin/assets/images/source/%E0%B8%81%E0%B8%B4%E0%B8%88%E0%B8%81%E0%B8%A3%E0%B8%A3%E0%B8%A1%203/H%2001.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"img-fluid\" src=\"https://www.klongri-ffs.com/klongri/admin/assets/images/source/%E0%B8%81%E0%B8%B4%E0%B8%88%E0%B8%81%E0%B8%A3%E0%B8%A3%E0%B8%A1%203/H%2003.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"img-fluid\" src=\"https://www.klongri-ffs.com/klongri/admin/assets/images/source/%E0%B8%81%E0%B8%B4%E0%B8%88%E0%B8%81%E0%B8%A3%E0%B8%A3%E0%B8%A1%203/H%2007.jpg\" style=\"height:100%; width:100%\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" class=\"img-fluid\" src=\"https://www.klongri-ffs.com/klongri/admin/assets/images/source/%E0%B8%81%E0%B8%B4%E0%B8%88%E0%B8%81%E0%B8%A3%E0%B8%A3%E0%B8%A1%203/H%2008.jpg\" style=\"height:100%; width:100%\" /></p>\r\n', 'klongri_58003920420191117_004507.jpg', 'true', '2019-11-16 17:45:07');
